import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpenseFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [filteredExpense, setFilteredExpense] = useState(props.items);
  const onSaveDateHandling = (selectedYear) => {
    setFilteredYear(selectedYear);
    console.log(selectedYear + "," + filteredYear);
    setFilteredExpense(props.items.filter( (items) =>
      items.date.getFullYear() === filteredYear
    ));
    console.log
  };
  return (
    <div>
      <ExpenseFilter onSaveDate={onSaveDateHandling} />
      <Card className="expenses">
        {filteredExpense.map((expense) => (
          <ExpenseItem
            key= {expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
