      id : Math.random().toString(),
      ...enteredExpenseData
    };
    props.onAddExpense(expenseData);
  }
  const [isFormVisible, setisFormVisible] = useState('no');
  const addNewExpenseButtonHandler =  () => {
    setisFormVisible('yes');
  }
  return (
    <div className="new-expense">
      {isFormVisible == 'no' && <button type="button" className="new-expense__actions" onClick={ad}>
            Add New Expense
          </button>}
      {isFormVisible == 'yes' && <ExpenseForm onSaveExpenseData = {saveExpenseDataHandler} />}
    </div>
  );
};

export default NewExpense;
