import Chart from "../Charts/Chart";

const ExpenseChart = props =>{
    const chartDataPoints = [
        {label: "Jan", value: 0},
        {label: "Feb", value: 0},
        {label: "Mar", value: 0},
        {label: "Apr", value: 0},
        {label: "May", value: 0},
        {label: "Jun", value: 0},
        {label: "July", value: 0},
        {label: "Aug", value: 0},
        {label: "Sep", value: 0},
        {label: "Nov", value: 0},
        {label: "Dec", value: 0}
    ];

    for(const expense in props.expenses){
        const expenseMonth = expense.date.getMonth();
        chartDataPoints[expenseMonth] += expense.amount;
    }

    return <Chart dataPoint = {chartDataPoints}/>
}
export default ExpenseChart;
